DECLARE @employee_name VARCHAR(100);
DECLARE @salary DECIMAL(10, 2);
SET @employee_name = 'John Smith';
SET @salary = 75000.50;
PRINT 'Employee: ' + @employee_name + ', Salary: ' + CAST(@salary AS VARCHAR);

output:
-- PL/SQL: Basic variable handling and printing
DECLARE
  v_employee_name VARCHAR2(100);
  v_salary NUMBER(10, 2);
BEGIN
  v_employee_name := 'John Smith';
  v_salary := 75000.50;
  DBMS_OUTPUT.PUT_LINE('Employee: ' || v_employee_name || ', Salary: ' || TO_CHAR(v_salary));
END;
/

