-- T-SQL: Error handling with TRY...CATCH
BEGIN TRY
    DECLARE @divisor INT = 0;
    DECLARE @result INT;
    SET @result = 100 / @divisor;
END TRY
BEGIN CATCH
    PRINT 'Error occurred: ' + ERROR_MESSAGE();
END CATCH;

output:
-- PL/SQL: Error handling with EXCEPTION
DECLARE
  v_divisor NUMBER := 0;
  v_result NUMBER;
BEGIN
  v_result := 100 / v_divisor;
EXCEPTION
  WHEN ZERO_DIVIDE THEN
    DBMS_OUTPUT.PUT_LINE('Error occurred: Division by zero.');
  WHEN OTHERS THEN
    DBMS_OUTPUT.PUT_LINE('Error occurred: ' || SQLERRM);
END;
/

