DECLARE @emp_id INT;
DECLARE emp_cursor CURSOR FOR SELECT EmployeeID FROM Employees WHERE Department = 'Sales';
OPEN emp_cursor;
FETCH NEXT FROM emp_cursor INTO @emp_id;
WHILE @@FETCH_STATUS = 0
BEGIN
    PRINT @emp_id;
    FETCH NEXT FROM emp_cursor INTO @emp_id;
END;
CLOSE emp_cursor;
DEALLOCATE emp_cursor;

